import React, { useState, useEffect, useRef, memo } from "react";
import Scrollbar from "./scrollbar";
function ChessTest() {
    const [data, setData] = useState(null);
    const [board, setBoard] = useState([]);
    const [moves, setMoves] = useState([]);
    const [selected, setSelected] = useState(null);
    const [aim, setAim] = useState(null);
    const svgContainerRef = useRef(null);
    const [containerSize, setContainerSize] = useState({ width: 0, height: 0 });
    const [turn, setTurn] = useState("white");
    const kb =
        "data:image/svg+xml;base64,";
    const qb =
        "data:image/svg+xml;base64,";
    const knb =
        "data:image/svg+xml;base64,";
    const bb =
        "data:image/svg+xml;base64,";
    const rb =
        "data:image/svg+xml;base64,";
    const pb =
        "data:image/svg+xml;base64,";
    const kw =
        "data:image/svg+xml;base64,";
    const qw =
        "data:image/svg+xml;base64,";
    const knw =
        "data:image/svg+xml;base64,";
    const bw =
        "data:image/svg+xml;base64,";
    const rw =
        "data:image/svg+xml;base64,";
    const pw =
        "data:image/svg+xml;base64,";
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('test.json'); // Assuming data.json is in the public folder
                const jsonData = await response.json();
                if (Array.isArray(jsonData) && jsonData.length > 0) {
                    setData(jsonData[0]);
                    console.log("fetched data")
                    console.log(jsonData[0]);
                }
                else {
                    setData({})
                    console.log("no data to fetch")
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []); // Empty dependency array to fetch data only once when the component mounts

    useEffect(() => {
        if (data) {
            parseFEN(data.game.state.fen);
            setTurn(data.game.state.turn);
        }
    }, [data])
    useEffect(() => {
        const updateContainerSize = () => {
            const container = svgContainerRef.current;
            if (container) {
                const containerRect = container.getBoundingClientRect();
                setContainerSize(prev => ({
                    width: containerRect.width,
                    height: containerRect.height
                }));
            }
        };
        updateContainerSize();
        window.addEventListener("resize", updateContainerSize);
        return () => {
            window.removeEventListener("resize", updateContainerSize);
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);
    useEffect(() => {
        if (!data || !selected)
            return;
        console.log(selected, data.game.state.possibleMoves)
        console.log("moves", data.game.state.possibleMoves[selected.y * 8 + selected.x])
        setMoves(data.game.state.possibleMoves[selected.y * 8 + selected.x]);
    }, [selected])
    useEffect(()=>{
        if (!aim) return;
        console.log("moving from", selected, "to ", aim);
        setSelected(null);
        setAim(null);
    }, [aim])
    const parseFEN = (fen) => {
        if (data === null)
            return
        const boardArray = [];
        const rows = fen.split('/');

        rows.forEach((row, rowIndex) => {
            let columnIndex = 0;
            row.split('').forEach((char) => {
                if (!isNaN(char)) {
                    // If the character is a number, it represents empty squares
                    for (let i = 0; i < parseInt(char); i++) {
                        boardArray.push({
                            type: "empty",
                            pos: { x: columnIndex, y: rowIndex },
                            color: null,
                            hasMoved: false
                        });
                        columnIndex++;
                    }
                } else {
                    // If the character represents a piece
                    let color = char === char.toUpperCase() ? "white" : "black";
                    let pieceType;
                    switch (char.toLowerCase()) {
                        case 'r':
                            pieceType = 'rook';
                            break;
                        case 'n':
                            pieceType = 'knight';
                            break;
                        case 'b':
                            pieceType = 'bishop';
                            break;
                        case 'q':
                            pieceType = 'queen';
                            break;
                        case 'k':
                            pieceType = 'king';
                            break;
                        case 'p':
                            pieceType = 'pawn';
                            break;
                        default:
                            break;
                    }
                    if (pieceType) {
                        boardArray.push({
                            type: pieceType,
                            pos: { x: columnIndex, y: rowIndex },
                            color: color,
                            hasMoved: false
                        });
                        columnIndex++;
                    }
                }
            });
        });
        console.log(boardArray);
        setBoard(boardArray);
    };
    const renderMoves = (x, y, ratio) => {
        if (!data || !selected || moves.length == 0) return;
        let isReachable = false;
        moves.map(e => {
            let m = pos(e);
            if (m && m.x === x && m.y === y) isReachable = true;
        });
        return isReachable
            ? <React.Fragment>
                <circle
                    cx={x * ratio + ratio / 2}
                    cy={y * ratio + ratio / 2}
                    r={ratio / 8}
                    fill="grey"
                />
                <rect
                    onClick={() => setAim(prev => ({ x: x, y: y }))}
                    x={x * ratio}
                    y={y * ratio}
                    width={ratio}
                    height={ratio}
                    fill="transparent"
                />
            </React.Fragment>
            : <circle cx="0" cy="0" r="0" />;
    };
    const renderElem = e => {
        ////console.log(e)
        let color = e.color;
        return (
            <div style={{ border: "solid white 1px", color: "white" }}>
                {e}
            </div>
        );
    };
    const Historic = () => {
        if (data === null)
            return
        let history = data.game.state.move_list;
        return history.map((e, index) => {
            return (
                <React.Fragment key={index}>
                    {renderElem(e)}
                </React.Fragment>
            );
        });
    };
    const renderSquare = (x, y, color) => {
        if (data === null)
            return
        const squareSize = containerSize.height * 0.8 / 8;
        return (
            <rect
                key={`${x}-${y}`}
                x={x * squareSize}
                y={y * squareSize}
                width={squareSize}
                height={squareSize}
                fill={color}
            />
        );
    };
    const renderPiece = (type, color, x, y, ratio) => {
        if (data === null)
            return
        let piece = "";
        if (color == null) return;
        ////console.log("rendering piece", type, color, x, y, ratio)
        switch (type) {
            case "knight":
                color === "black" ? (piece = knb) : (piece = knw);
                break;
            case "bishop":
                color === "black" ? (piece = bb) : (piece = bw);
                break;
            case "king":
                color === "black" ? (piece = kb) : (piece = kw);
                break;
            case "queen":
                color === "black" ? (piece = qb) : (piece = qw);
                break;
            case "rook":
                color === "black" ? (piece = rb) : (piece = rw);
                break;
            case "pawn":
                color === "black" ? (piece = pb) : (piece = pw);
                break;
            default:
                break;
        }
        if ((turn % 2 && color === "black") || (!(turn % 2) && color === "white"))
            return (
                <image
                    onClick={() =>
                        setSelected(prev => ({ x: x, y: y }))}
                    x={x * ratio}
                    y={y * ratio}
                    width={ratio}
                    height={ratio}
                    xlinkHref={piece}
                />
            );
        else
            return (
                <image
                    x={x * ratio}
                    y={y * ratio}
                    width={ratio}
                    height={ratio}
                    xlinkHref={piece}
                    style={{ pointerEvents: "none" }}
                />
            );
    };
    const renderPieces = () => {
        if (data === null)
            return
        const colors = ["#FFCE9E", "#D18B47"];
        return board.map((e, index) => {
            return (
                <React.Fragment key={index}>
                    {renderSquare(e.pos.x, e.pos.y, colors[(e.pos.x + e.pos.y) % 2])}
                    {renderPiece(
                        e.type,
                        e.color,
                        e.pos.x,
                        e.pos.y,
                        containerSize.height * 0.8 / 8
                    )}
                    {renderMoves(e.pos.x, e.pos.y, containerSize.height * 0.8 / 8)}
                </React.Fragment>
            );
        });
    };
    function pos(move) {
        if (!move)
            return (null);
        const fileMap = {
            'a': 0,
            'b': 1,
            'c': 2,
            'd': 3,
            'e': 4,
            'f': 5,
            'g': 6,
            'h': 7
        };
    
        const rankMap = {
            '1': 7,
            '2': 6,
            '3': 5,
            '4': 4,
            '5': 3,
            '6': 2,
            '7': 1,
            '8': 0
        };
    
        const from = move.substring(0, 2);
        const to = move.substring(2, 4);
    
        const fromFile = fileMap[from.charAt(0)];
        const fromRank = rankMap[from.charAt(1)];
    
        return {x: fromFile, y: fromRank};
    }
    
    return (
        <div
            ref={svgContainerRef}
            style={{
                width: "70vw",
                height: "70vh",
                position: "relative",
                backgroundColor: "transparent",
                opacity: 1,
                paddingBottom: "10 vh",
                flexDirection: "row"
            }}
        >
            <svg
                width={containerSize.height * 0.8}
                height={containerSize.height * 0.8}
                style={{ border: "solid black 2px", boxShadow: "5px 5px 5px black" }}
            >
                {renderPieces()}
            </svg>
            <div
                style={{
                    border: "solid black 2px",
                    maxHeight: "10vh",
                    background: "linear-gradient(50deg, magenta, blue)",
                    flexDirection: "column",
                    width: "100%"
                }}
            >
                <Scrollbar>
                    {Historic()}
                </Scrollbar>
            </div>
        </div>
    );
};

export default ChessTest;